select prop_id as "Proposal", rpad(last_name, 27) as "P.I.", hst_app as "HST orbits", xmm_app as "XMM ksec", noao_app as "NOAO night", nrao_app as "NRAO hrs", spitzer_app as "Spitzer hrs", swift_app as "Swift ksec", nustar_app as "NuSTAR ksec"  from proposal where (hst_app > 0 or xmm_app > 0 or noao_app > 0 or nrao_app > 0 or spitzer_app > 0 or swift_app > 0 or nustar_app > 0) and prop_status = 'Y' order by last_name;
